function isEmpty(obj) {
    if (obj == null || obj == undefined || obj == "" || obj == "undefined" || obj == "null") {
        return true;
    }
    return false;
}
/**
 * 判断手机号码是否正确
 *
 * **/
function checkIsMobile(mobile) {

    if (isEmpty(mobile) || !(/^1[3|4|5|8|7][0-9]\d{4,8}$/.test(mobile))) {
        return false;
    }
    return true;
}
