app.directive("slideBox",["$rootScope","$http",function($rootScope, $http){
    return {
        restrict: 'AE',
        template: '<div class="bd"><ul></ul></div><div class="hd"><ul></ul></div>',
        link: function(scope, iElement, iAttrs){
            var watchbanners = scope.$watch("banners",function(newVal,oldVal){
                if(newVal != undefined && newVal.length > 1 ){
                    //轮播图
                    var imgList = scope.banners;
                    var liList = "";
                    for(var i=0;i<imgList.length;i++){
                        liList += '<li id="'+imgList[i].id+'"><img src="'+imgList[i].imgurl+'" type="'+imgList[i].type+'" id="'+imgList[i].adid+'" title="'+imgList[i].title+'"></li>';
                    }
                    $(iElement).find("ul").html(liList);
                    TouchSlide({
                        slideCell:"#" + iAttrs.id,
                        titCell:".hd ul", //开启自动分页 autoPage:true ，此时设置 titCell 为导航元素包裹层
                        mainCell:".bd ul",
                        effect:"leftLoop",
                        interTime: 5000,//自动运行间隔
                        delayTime: 1000,//切换效果持续时间
                        autoPage:true,//自动分页
                        autoPlay:true //自动播放
                    });
                    //释放监听
                    watchbanners();
                }else if(newVal != undefined && newVal.length == 1){
                    //轮播图
                    var imgList = scope.banners;
                    var liList = "";
                    for(var i=0;i<imgList.length;i++){
                        liList += '<li id="'+imgList[i].id+'"><img src="'+imgList[i].imgurl+'" type="'+imgList[i].type+'" id="'+imgList[i].adid+'" title="'+imgList[i].title+'"></li>';
                    }
                    $(iElement).find("ul").html(liList);
                    TouchSlide({
                        slideCell:"#" + iAttrs.id,
                        titCell:".hd ul", //开启自动分页 autoPage:true ，此时设置 titCell 为导航元素包裹层
                        mainCell:".bd ul",
                        effect:"leftLoop",
                        autoPage:true,//自动分页
                        autoPlay:false //自动播放
                    });
                    //释放监听
                    watchbanners();
                }
            })
        }
    }
}]);
